-- CAF-233: Case Management - Case Issues - Issue Field
-- The Issue Field is a conditional field based on the tab name field in the Case Management - Case Issues and Resolution table
-- The code table below defines all the front-end field names and is used as a lookup for AEFI fields

define _APP_USER_ROLE = rl__caefiss_app;
WHENEVER SQLERROR EXIT FAILURE ROLLBACK;
SET ECHO ON
SPOOL 9_CAF-233.log
ALTER SESSION SET CURRENT_SCHEMA=CAEFISS_OWNER;

-- Create code table for AEFI fields
CREATE TABLE CD_AEFI_FIELDS
(
  ID               NUMBER(15),
  ENGLISH_VALUE    VARCHAR2(500 CHAR) NOT NULL,
  FRENCH_VALUE     VARCHAR2(500 CHAR) NOT NULL,
  AEFI_TABS_ID     NUMBER(15),
  DATE_CREATED     DATE               NOT NULL,
  USER_CREATED     VARCHAR2(30 CHAR)  NOT NULL,
  DATE_UPDATED     DATE,
  USER_UPDATED     VARCHAR2(30 CHAR),
  STATUS           NUMBER(1)          NOT NULL,
  VERSION          NUMBER(5)          NOT NULL,
  SORTORDER        NUMBER(15),
  CONSTRAINT PK_AEFI_FIELDS PRIMARY KEY (ID),
  CONSTRAINT FK_AEFI_FIELDS_AEFI_TABS_ID FOREIGN KEY (AEFI_TABS_ID) REFERENCES CD_AEFI_TABS (ID)
);

CREATE TABLE CD_AEFI_FIELDS_AUD
(
  ID               NUMBER(15),
  ENGLISH_VALUE    VARCHAR2(500 CHAR) NOT NULL,
  FRENCH_VALUE     VARCHAR2(500 CHAR) NOT NULL,
  AEFI_TABS_ID     NUMBER(15),
  DATE_CREATED     DATE               NOT NULL,
  USER_CREATED     VARCHAR2(30 CHAR)  NOT NULL,
  DATE_UPDATED     DATE,
  USER_UPDATED     VARCHAR2(30 CHAR),
  STATUS           NUMBER(1)          NOT NULL,
  VERSION          NUMBER(5)          NOT NULL,
  SORTORDER        NUMBER(15)
);

-- Sequence for CD_AEFI_FIELDS(ID)
CREATE SEQUENCE AEFI_FIELDS_ID_SEQ INCREMENT BY 1 START WITH 600 CACHE 2;

-- Permissions
GRANT SELECT, ALTER ON AEFI_FIELDS_ID_SEQ TO &_APP_USER_ROLE;

GRANT INSERT, SELECT, UPDATE, DELETE ON CD_AEFI_FIELDS TO &_APP_USER_ROLE;

GRANT INSERT, SELECT ON CD_AEFI_FIELDS_AUD TO &_APP_USER_ROLE;

GRANT INSERT, SELECT, UPDATE, DELETE ON CAEFISS_OWNER.CD_AEFI_FIELDS TO RL__CAEFISS_LOAD_ETL;

GRANT INSERT, SELECT ON CAEFISS_OWNER.CD_AEFI_FIELDS_AUD TO RL__CAEFISS_LOAD_ETL;

